import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';

class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }

    }
    componentDidMount() {

         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); 

    }
    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={film.id} id={film.id} xs={10} md={8} className="text-center border-top border-bottom py-3 mx-auto">
       <a className="h3  ">{film.title}</a><br /><br /><p className=" text-muted small">{film.release_date} | {film.rt_score}%</p>
    </Col>

    );

        return(
          <Row>
            <Col xs={12}  className="w-100 justify-content-end d-flex mb-5" > <DropdownButton id="dropdown-basic-button" title="Filter Search">
  <Dropdown.Item className="w-100" href="">Action &#f0de; <FontAwesomeIcon icon="check-square" /> <i className="fas fa-sort-up"></i></Dropdown.Item>
  <Dropdown.Item href="">Another action</Dropdown.Item>
  <Dropdown.Item href="">Something else</Dropdown.Item>
</DropdownButton></Col>
            {films}
          </Row>
        );
    }

}
export default FilmsPage;
