import React, { Component } from "react";
import { Link } from "react-router-dom";
import Col from "react-bootstrap/Col";

class Species extends Component {
  constructor() {
    super();
    this.state = {
      end: false,
      character: []
    };
  }
  componentDidMount() {
    let arr = [];

      fetch(`${this.props.info.species}`)
        .then((res) => res.json())
        .then((obj) => {
          var person = {
  name: `${this.props.info.name}`,
  species: obj.name,
  eyes: `${this.props.info.eye_color}`,
  hair: `${this.props.info.hair_color}`
};
          arr.push(obj);
          this.setState({
            films: arr,
          });
        })

  }

  render() {
    const films = this.state.films.map((film) => (
      <li>
        <Link to={`/films/${film.id}`}>{film.title}</Link>
      </li>
    ));

    return (
      <>
        <Col xs={2}>
          <b>Film(s):</b>
        </Col>{" "}
        <Col xs={10}>
          <ul style={{ listStyleType: "none" }}>{films}</ul>
        </Col>
      </>
    );
  }
}
export default Species;
