import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSortUp } from '@fortawesome/free-solid-svg-icons';
import { faSortDown } from '@fortawesome/free-solid-svg-icons';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';

class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }
        this.sortIntUp = this.sortIntUp.bind(this);

    }
    componentDidMount() {

         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); }
  sortIntUp(){
      let arr = this.state.cardDetails;
    for(let i = 0; i < arr.length; i++){
        for(let j = 0; j < arr.length; j++){

            if(parseInt(arr[i].rt_score) < parseInt(arr[j].rt_score)){
                let temp = arr[i];
                arr[i] = arr[j];
                arr[j] = temp;
            }

        }
    }

     this.setState({
                cardDetails: arr
            }) ;
      alert(this.state.cardDetails[0].rt_score);
  }

    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={film.id} id={film.id} xs={10} md={8} className="text-center border-top border-bottom py-3 mx-auto">
       <a className="h3  ">{film.title}</a><br /><br /><p className=" text-muted small">{film.release_date} | {film.rt_score}%</p>
    </Col>

    );

        return(
          <Row>
            <Col xs={12}  className="w-100 justify-content-end d-flex mb-5" > <DropdownButton id="dropdown-basic-button" variant="outline-primary" title="Filter Search">
  <Dropdown.Item className="w-100" href="#" onClick={this.sortIntUp}>Rating <FontAwesomeIcon className="ml-2" icon={faSortUp} /></Dropdown.Item>
  <Dropdown.Item className="w-100" href="#">Rating <FontAwesomeIcon className="ml-2" icon={faSortDown} /></Dropdown.Item>
  <Dropdown.Item className="w-100" href="#">Release Date <FontAwesomeIcon className="ml-2" icon={faSortUp} /></Dropdown.Item>
  <Dropdown.Item className="w-100" href="#">Release Date <FontAwesomeIcon className="ml-2" icon={faSortDown} /></Dropdown.Item>
  <Dropdown.Item className="w-100" href="#">Title <FontAwesomeIcon className="ml-2" icon={faSortUp} /></Dropdown.Item>
  <Dropdown.Item className="w-100" href="#">Title <FontAwesomeIcon className="ml-2" icon={faSortDown} /></Dropdown.Item>
</DropdownButton></Col>
            {films}
          </Row>
        );
    }

}
export default FilmsPage;
