import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';

class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }

    }
    componentDidMount() {

         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); 

    }
    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={film.id} id={film.id} xs={10} md={8} className="d-flex  justify-content-center border-top border-bottom py-5 mx-auto">
       <a className="h3 align-self-center">{film.title}</a> <p className="text-muted small ml-auto mr-md-5 mr-1">{film.release_date} | {film.rt_score}%</p>
    </Col>

    );

        return(
          <Row>
            {films}
          </Row>
        );
    }

}
export default FilmsPage;
