import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';



class FilmInfo extends Component {
    constructor(){
        super();
        this.state = {
            render: false,
            cardDetails: []

        }

    }
    componentDidMount() {
       const{match: {params}} = this.props;
     let arr = [];
        fetch(`https://ghibliapi.herokuapp.com/films/${params.id}`)
            .then(res =>  res.json())
            .then(obj => {
            arr[0] = obj;

             this.setState({
                cardDetails: arr,
                 render: true
            })

        });



    }



    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={this.state.render ? film.id : null} xs={12}  className="w-100">
    <Row className="d-inline-block">
        <main>
       <Col xs={12} md={8} className="order-2 order-md-1 mx-0 px-0">

      {this.state.render ? film.description : null}


        </Col> 
        <Col xs={12} md={4}className="order-1 order-md-2">
             <aside className="text-right">
            <b>Director:</b> {this.state.render ? film.director : null}
        </aside>
        </Col>
        </main>
    </Row>

    </Col>

    );

        return(
          <Row >
  {films}
          </Row>
        );
    }

}
export default FilmInfo;
