import React , {Component} from 'react';

class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }

    }
    componentDidMount() {
        alert(this.props.match)
         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); }
    render(){
        return(
           <h1>This is the films page</h1>
        );
    }

}
export default FilmsPage;
