import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Image from 'react-bootstrap/Image';
import Logo from './../assets/logo.png';


class FilmInfo extends Component {
    constructor(){
        super();
        this.state = {
            render: false,
            cardDetails: []

        }

    }
    componentDidMount() {
       const{match: {params}} = this.props;
     let arr = [];
        fetch(`https://ghibliapi.herokuapp.com/films/${params.id}`)
            .then(res =>  res.json())
            .then(obj => {
            arr[0] = obj;

             this.setState({
                cardDetails: arr,
                 render: true
            })

        });



    }



    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={this.state.render ? film.id : null} xs={12}  className="w-100">
    <Row className="w-100 p-3">

       <Col xs={12} lg={7} className="order-2 order-lg-1 m-0 ">

           <p className="font-italic"><b >{this.state.render ? film.title : null}</b>- {this.state.render ? film.description : null}</p>


        </Col> 
        <Col xs={12} lg={5}className="order-1 order-lg-2 mb-3 mb-lg-0">
             <aside className="text-center border border-secondary p-2">
                 <p style={{fontFamily: 'cursive'}} className="h4 mt-2">{this.state.render ? film.title : null}</p>
                 <Image src={Logo} fluid className="bg-primary "/>
                 <section style={{ lineHeight: "2"}} className="mt-4 ">

                     <Row><Col xs={2}><b>Director:</b></Col> <Col xs={10}>{this.state.render ? film.director : null}</Col></Row>
                      <Row><Col xs={2}><b>Producer:</b></Col> <Col xs={10}>{this.state.render ? film.producer : null}</Col></Row>
                     <Row><Col xs={2}><b>Rating:</b></Col> <Col xs={10}>{this.state.render ? film.rt_score : null}%</Col></Row>
                      <Row><Col xs={6}><b>Release Date:</b></Col> <Col xs={4}>{this.state.render ? film.release_date : null}</Col></Row>


                 </section>
        </aside>
        </Col>

    </Row>

    </Col>

    );

        return(
          <Row >
  {films}
          </Row>
        );
    }

}
export default FilmInfo;
