import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';



class FilmInfo extends Component {
    constructor(){
        super();
        this.state = {
            render: false,
            cardDetails: []

        }

    }
    componentDidMount() {
       const{match: {params}} = this.props;
     let arr = [];
        fetch(`https://ghibliapi.herokuapp.com/films/${params.id}`)
            .then(res =>  res.json())
            .then(obj => {
            arr[0] = obj;
            alert(arr[0].title);
             this.setState({
                cardDetails: arr,
                 render: true
            })
              alert(this.state.cardDetails[0].title);
        });



    }



    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={this.state.render ? film.id : null} xs={10} md={8} className="text-center border-secondary border-top  py-5 mx-auto">
    <section>
      <p>{this.state.render ? film.description : null} </p>
                                                          </section>
    </Col>

    );

        return(
          <Row >
         <Col xs={12} className=""><Row><Col xs={12} md={4}  lg={3}  className=""></Col><Col xs={12} md={4}  lg={6} style={{fontFamily: 'cursive'}}className="text-center display-3 h1 mb-md-5 mb-2">{this.state.render ? this.state.cardDetails[0].title : null}</Col><Col xs={12} md={4}  lg={3} className="mt-lg-3 mt-md-4 mt-0 mb-3 mb-md-0"></Col></Row></Col>
            {films}
          </Row>
        );
    }

}
export default FilmInfo;
