import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }

    }
    componentDidMount() {

         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); 

    }
    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={film.id} id={film.id} xs={10} md={8} className="text-center border-top border-bottom py-3 mx-auto">
       <a className="h3  ">{film.title}</a><br /><br /><p className=" text-muted small">{film.release_date} | {film.rt_score}%</p>
    </Col>

    );

        return(
          <Row>
            <Col xs={12}  className="w-100 justify-content-end d-flex mb-5" > <Form.Control as="select" className="w-25" size="sm" custom>
      <option className="text-center"  value="" disabled selected>Filter Search</option>
       <option>Rating  <FontAwesomeIcon icon={['fab', 'apple']} /></option>
      <option>Rating &#xe252;</option>
      <option>Release Date &#xe253;</option>
      <option>Release Date &#xe252;</option>
      <option>Title &#xe253;</option>
      <option>Title &#xe252;</option>
    </Form.Control></Col>
            {films}
          </Row>
        );
    }

}
export default FilmsPage;
