import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }

    }
    componentDidMount() {

         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); 

    }
    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={film.id} id={film.id} xs={10} className="d-flex align-items-center border  border-top border-bottom py-5 mx-auto">
       <a className="h1">{film.title}</a>
    </Col>

    );

        return(
          <Row>
            {films}
          </Row>
        );
    }

}
export default FilmsPage;
