          } catch (err) {
            console.log(err);
          }
        } else if (val === "release") {
          try {
            val1 = parseInt(arr[i].release_date);
            val2 = parseInt(arr[j].release_date);
          } catch (err) {
            console.log(err);
          }
        } else {
          try {
