import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';
import Filter from './Filter';


class FilmsPage extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            cardDetails: []

        }
        this.sortUp = this.sortUp.bind(this);
this.sortDown = this.sortDown.bind(this);
    }
    componentDidMount() {

         let arr = [];
        fetch("https://ghibliapi.herokuapp.com/films")
            .then(res =>  res.json())
            .then(obj => {
            obj.forEach( (val) => {
                arr.push(val);
            })
             this.setState({
                cardDetails: arr
            })
        }); }
  sortUp(val){
      let arr = this.state.cardDetails;
    for(let i = 0; i < arr.length; i++){
        for(let j = 0; j < arr.length; j++){
            let val1, val2;
            if(val === "rating"){
                val1 = parseInt(arr[i].rt_score);
                val2 = parseInt(arr[j].rt_score);
            }
            else if(val === "release"){
                val1 = parseInt(arr[i].release_date);
                val2 = parseInt(arr[j].release_date);
            }
            else{
                val1 = (arr[i].title).toLowerCase();
                val2 = (arr[j].title).toLowerCase();
            }
            if(val1 < val2){
                let temp = arr[i];
                arr[i] = arr[j];
                arr[j] = temp;
            }

        }
    }

     this.setState({
                cardDetails: arr
            }) ;
  }
  sortDown(val){
      let arr = this.state.cardDetails;
    for(let i = 0; i < arr.length; i++){
        for(let j = 0; j < arr.length; j++){
            let val1, val2;
            if(val === "rating"){
                val1 = parseInt(arr[i].rt_score);
                val2 = parseInt(arr[j].rt_score);
            }
            else if(val === "release"){
                val1 = parseInt(arr[i].release_date);
                val2 = parseInt(arr[j].release_date);
            }
            else{
                val1 = (arr[i].title).toLowerCase();
                val2 = (arr[j].title).toLowerCase();
            }
            if(val1 > val2){
                let temp = arr[i];
                arr[i] = arr[j];
                arr[j] = temp;
            }

        }
    }

     this.setState({
                cardDetails: arr
            }) ;
  }

    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={film.id} id={film.id} xs={10} md={8} className="text-center border-top border-bottom py-3 mx-auto">
       <a className="h3  ">{film.title}</a><br /><br /><p className=" text-muted small">{film.release_date} | {film.rt_score}%</p>
    </Col>

    );

        return(
          <Row  className="mb-3 ">
            <Col xs={12} md={9} className=" mb-3 text-center text-md-right h1">YOGJFFJVKFEJF</Col>
            <Col xs={12}  md={3} className=" text-center w-100" ><Filter up={this.sortUp}  down={this.sortDown} /></Col>
            {films}
          </Row>
        );
    }

}
export default FilmsPage;
