import React , {Component} from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Image from 'react-bootstrap/Image';
import Logo from './../assets/logo.png';


class FilmInfo extends Component {
    constructor(){
        super();
        this.state = {
            render: false,
            cardDetails: []

        }

    }
    componentDidMount() {
       const{match: {params}} = this.props;
     let arr = [];
        fetch(`https://ghibliapi.herokuapp.com/films/${params.id}`)
            .then(res =>  res.json())
            .then(obj => {
            arr[0] = obj;

             this.setState({
                cardDetails: arr,
                 render: true
            })

        });



    }



    render(){

             const films = this.state.cardDetails.map((film) =>
    <Col key={this.state.render ? film.id : null} xs={12}  className="w-100">
    <Row className="w-100 p-3">

       <Col xs={12} md={7} className="order-2 order-md-1 m-0 ">

           <p>{this.state.render ? film.description : null}</p>


        </Col> 
        <Col xs={12} md={5}className="order-1 order-md-2">
             <aside className="text-center border border-secondary px-2">
                 <p style={{fontFamily: 'cursive'}} className="h4 mt-2">{this.state.render ? film.title : null}</p>
                 <Image src={Logo} fluid className="bg-primary "/>
                 <section className="mt-5 text-left">
                 <p><b>Director:</b> {this.state.render ? film.director : null}</p>
                 </section>
        </aside>
        </Col>

    </Row>

    </Col>

    );

        return(
          <Row >
  {films}
          </Row>
        );
    }

}
export default FilmInfo;
