import React , {Component} from 'react';
import {BrowserRouter as Router, Link, Switch, Route} from 'react-router-dom';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import FilmsFilter from './FilmsFilter';


class ReturnFilms extends Component {
    constructor(){
        super();
        this.state = {
            end: false,
            films: []

        }

    }
    componentDidMount() {

         let arr = [];
        alert(this.props.movie.id)
        fetch("https://ghibliapi.herokuapp.com/films/"+this.props.movie.id+"")
            .then(res =>  res.json())
            .then(obj => {
            arr[0] = obj;
            alert(arr[0].title)
             this.setState({
                films: arr
            })
        }); }


    render(){

             const films = this.state.films.map((film) =>
    <li><Link  to={`films/${film.id}`} >{film.title}</Link></li>

    );

        return(
         <><Col xs={2}><b>Films:</b></Col> <Col xs={10}><ul>{films}</ul></Col></>
        );
    }

}
export default ReturnFilms;
